	const parameters = {
		expiration: 'never',
		name: 'CodeStream',
		scope: 'read,write',
		response_type: 'token',
		key,
		callback_method: 'fragment',
		return_url: `https://${host}:${port}/no-auth/provider-token/trello?state=${state}`
